mapper.select:
select id, title, content, img, heartCnt, hitCnt, board_id, user_id, place_id,
       case when date_format(create_date, '%Y%m%d') = date_format(now(), '%Y%m%d')
                then date_format(create_date, '%H:%i:%s')
            else date_format(create_date, '%y.%m.%d')
           end create_date,
       case when date_format(update_date, '%Y%m%d') = date_format(now(), '%Y%m%d')
                then date_format(update_date, '%H:%i:%s')
            else date_format(update_date, '%y.%m.%d')
           end update_date
       from boardDetail
where id = #{id}

select id, title, content, img, heartCnt, hitCnt, board_id, user_id, place_id,
       case when date_format(create_date, '%Y%m%d') = date_format(now(), '%Y%m%d')
                then date_format(create_date, '%H:%i:%s')
            else date_format(create_date, '%y.%m.%d')
           end create_date,
       case when date_format(update_date, '%Y%m%d') = date_format(now(), '%Y%m%d')
                then date_format(update_date, '%H:%i:%s')
            else date_format(update_date, '%y.%m.%d')
           end update_date
from boardDetail

select id, title, content, img, heartCnt, hitCnt, board_id, user_id, place_id,
    case when date_format(create_date, '%Y%m%d') = date_format(now(), '%Y%m%d')
            then date_format(create_date, '%H:%i:%s')
        else date_format(create_date, '%y.%m.%d')
        end create_date,
    case when date_format(update_date, '%Y%m%d') = date_format(now(), '%Y%m%d')
            then date_format(update_date, '%H:%i:%s')
        else date_format(update_date, '%y.%m.%d')
        end update_date
from boardDetail
where board_id = #{boardId}

mapper.update:
update BoardDetail
set title = #{title}, content = #{content}, img = #{img}, board_id = #{boardId}, update_date = now()
where id = #{id}

update BoardDetail
set hitCnt = #{hitCnt}, heartCnt = #{heartCnt}
where id = #{id}
